import styled from "styled-components";

export const ButtonContainer = styled.button`

    background-color: #FAFAFA;
    border: 1px solid #FAFAFA;
    border-radius: 20px;

    height: 52px;
    width: 80%;

    margin: 20px;
    font-size: 24px;
    &: hover {
        background-color: #FAFAFA20;
        color: #fff;
        cursor: pointer;
    }

`
