import styled from "styled-components";

export const InputContainer = styled.div`

    border: 0px solid #FAFAFA;
    
    height: 62px;
    width: 80%;

    margin: 20px;
    
    
    input {
        
        background: transparent;
        border: 1px solid ;
        width: 91%;
        border-radius: 20px;
        height: 62px;
        padding: 0 20px;
        color: #FFFFFF;
        font-size: 20px
    }

`
